import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Skeleton from 'react-loading-skeleton';
import { NavLink } from 'react-router-dom';

function Product() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editedProduct, setEditedProduct] = useState({
    title: '',
    price: '',
    description: '',
    image: '',
    category: '',
  });

  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      const response = await fetch(`https://fakestoreapi.com/products/${id}`);
      const data = await response.json();
      setProduct(data);
      setEditedProduct(data);
      setLoading(false);
    };
    getProduct();
  }, [id]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEditedProduct((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleEdit = () => {
    fetch(`https://fakestoreapi.com/products/${id}`, {
      method: 'PUT',
      body: JSON.stringify(editedProduct),
    })
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        // Redirect to the edited product page or any other relevant page
        navigate.push(`/product/${id}`);
      })
      .catch((error) => {
        console.error('Error editing product:', error);
      });
  };

    const Loading = () => {
        return (
            <>
                <div className="row d-flex justify-content-center">
                    <div className="col-md-12">
                        <NavLink className="text-decoration-none text-dark" to={`/`}>
                            <div className="d-flex align-items-center m-3">
                                <Skeleton height={20} width={50} />
                            </div>
                        </NavLink>
                        <div>
                            <div className="row">
                                <div className="col-md-6">
                                    <div className="images p-3">
                                        <div className="text-center p-4">
                                            <Skeleton height={300} width={250} />
                                        </div>
                                    </div>
                                </div>
                                <div className="col-md-6">
                                    <div className="border p-4">
                                        <div className="mt-4 mb-3"> <span className="text-uppercase text-muted brand">                                                <Skeleton height={30} width={150} />
                                        </span>
                                            <h5 className="text-uppercase">
                                                <Skeleton height={30} width={200} />
                                            </h5>
                                            <div className="price d-flex flex-row align-items-center">
                                                <span className="act-price">
                                                    <Skeleton height={20} width={70} />
                                                    <Skeleton height={30} width={100} />
                                                </span>
                                            </div>
                                        </div>
                                        <p className="about">
                                            <Skeleton height={10} width={300} />
                                            <Skeleton height={10} width={300} />
                                            <Skeleton height={10} width={300} />
                                            <Skeleton height={10} width={300} />
                                        </p>
                                        <div className="cart mt-4 align-items-center">
                                            <Skeleton height={40} width={150} />

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }

    const ShowDetails = () => {
        return (
        <>
            <div className="row d-flex justify-content-center">
            {/* Rest of the code */}
            <div className="col-md-6">
                <div className="border p-4">
                <div className="mt-4 mb-3">
                    <span className="text-muted text-capitalize">
                    in {editedProduct.category}
                    </span>
                    <h5 className="text-uppercase">{editedProduct.title}</h5>
                    <div className="form-group">
                    <label htmlFor="price">Price:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="price"
                        name="price"
                        value={editedProduct.price}
                        onChange={handleInputChange}
                    />
                    </div>
                    <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        value={editedProduct.description}
                        onChange={handleInputChange}
                    />
                    </div>
                    {/* Include other input fields as needed */}
                </div>
                <div className="cart mt-4 d-flex align-items-center justify-content-center">
                    <button
                    className="btn btn-outline-dark text-uppercase mr-2 px-4"
                    onClick={handleEdit}
                    >
                    Edit
                    </button>
                </div>
                </div>
            </div>
            </div>
        </>
        );
    };

    return (
        <>
        <div className="container px-0 mb-5" style={{ marginTop: '66px' }}>
            {loading ? <Loading /> : <ShowDetails />}
        </div>
        </>
    );
}

export default Product;
